(function(){tinymce.create('tinymce.plugins.Link_urlPlugin',{init:function(ed,url){ed.addCommand('mceLink_url',function(){ed.windowManager.open({file:url+'/dialog.php',width:420,height:400,inline:1},{plugin_url:url,})});ed.addButton('link_url',{title:'link_url.desc',cmd:'mceLink_url',image:url+'/img/plus.gif'})},getInfo:function(){return{longname:'Link_url plugin',author:'Hieu',version:"1.0"}}});tinymce.PluginManager.add('link_url',tinymce.plugins.Link_urlPlugin)})();